components.css:
.btn-gradient {
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 600;
  color: white;
  background: linear-gradient(135deg, #8b5cf6 0%, #ec4899 100%);
  border: none;
  border-radius: 8px;
  cursor: pointer;
  transition: transform 0.2s, box-shadow 0.2s;
}

.btn-gradient:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 20px rgba(139, 92, 246, 0.3);
}

.btn-glow {
  padding: 14px 28px;
  font-size: 16px;
  font-weight: 600;
  color: #22d3ee;
  background: transparent;
  border: 2px solid #22d3ee;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s;
  position: relative;
  overflow: hidden;
}

.btn-glow:hover {
  color: white;
  background: #22d3ee;
  box-shadow: 0 0 20px #22d3ee, 0 0 40px #22d3ee, 0 0 60px #22d3ee;
}

.btn-3d {
  padding: 14px 28px;
  font-size: 16px;
  font-weight: 700;
  color: white;
  background: #8b5cf6;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  box-shadow: 0 6px 0 #6d28d9;
  transition: all 0.1s;
}

.btn-3d:active {
  transform: translateY(4px);
  box-shadow: 0 2px 0 #6d28d9;
}

.card-glass {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 16px;
  padding: 2rem;
  text-align: center;
  color: white;
  max-width: 300px;
}

.card-glass .card-icon {
  font-size: 3rem;
  margin-bottom: 1rem;
}

.card-glass h3 {
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
}

.card-glass p {
  opacity: 0.8;
  font-size: 0.95rem;
}

.card-hover {
  background: #1f1f23;
  border-radius: 16px;
  overflow: hidden;
  max-width: 300px;
  transition: transform 0.3s, box-shadow 0.3s;
}

.card-hover:hover {
  transform: translateY(-8px);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.4);
}

.card-hover .card-image {
  height: 150px;
  background: linear-gradient(135deg, #8b5cf6, #ec4899);
}

.card-hover .card-content {
  padding: 1.5rem;
}

.card-hover h3 {
  color: white;
  margin-bottom: 0.5rem;
}

.card-hover p {
  color: #a1a1aa;
  font-size: 0.9rem;
}

.loader-spinner {
  width: 48px;
  height: 48px;
  border: 4px solid rgba(139, 92, 246, 0.2);
  border-top-color: #8b5cf6;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  to { transform: rotate(360deg); }
}

.loader-dots {
  display: flex;
  gap: 8px;
}

.loader-dots .dot {
  width: 12px;
  height: 12px;
  background: #8b5cf6;
  border-radius: 50%;
  animation: bounce 1.4s ease-in-out infinite;
}

.loader-dots .dot:nth-child(1) { animation-delay: 0s; }
.loader-dots .dot:nth-child(2) { animation-delay: 0.2s; }
.loader-dots .dot:nth-child(3) { animation-delay: 0.4s; }

@keyframes bounce {
  0%, 80%, 100% { transform: scale(0.6); opacity: 0.5; }
  40% { transform: scale(1); opacity: 1; }
}

.loader-pulse {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  background: #8b5cf6;
  animation: pulse-ring 1.5s ease-out infinite;
}

@keyframes pulse-ring {
  0% { transform: scale(0.5); opacity: 1; }
  100% { transform: scale(1.5); opacity: 0; }
}

.switch-toggle {
  position: relative;
  display: inline-block;
  width: 52px;
  height: 28px;
}

.switch-toggle input {
  opacity: 0;
  width: 0;
  height: 0;
}

.switch-toggle .slider {
  position: absolute;
  cursor: pointer;
  inset: 0;
  background: #3f3f46;
  border-radius: 28px;
  transition: 0.3s;
}

.switch-toggle .slider:before {
  content: '';
  position: absolute;
  height: 22px;
  width: 22px;
  left: 3px;
  bottom: 3px;
  background: white;
  border-radius: 50%;
  transition: 0.3s;
}

.switch-toggle input:checked + .slider {
  background: #8b5cf6;
}

.switch-toggle input:checked + .slider:before {
  transform: translateX(24px);
}

.input-floating {
  position: relative;
  width: 280px;
}

.input-floating input {
  width: 100%;
  padding: 16px 12px 8px;
  font-size: 16px;
  color: white;
  background: #27272a;
  border: 2px solid #3f3f46;
  border-radius: 8px;
  outline: none;
  transition: border-color 0.3s;
}

.input-floating label {
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY(-50%);
  color: #71717a;
  font-size: 16px;
  pointer-events: none;
  transition: all 0.3s;
}

.input-floating input:focus,
.input-floating input:not(:placeholder-shown) {
  border-color: #8b5cf6;
}

.input-floating input:focus + label,
.input-floating input:not(:placeholder-shown) + label {
  top: 8px;
  font-size: 12px;
  color: #8b5cf6;
}

.input-search {
  position: relative;
  display: flex;
  align-items: center;
  width: 280px;
}

.input-search svg {
  position: absolute;
  left: 12px;
  color: #71717a;
}

.input-search input {
  width: 100%;
  padding: 12px 12px 12px 44px;
  font-size: 15px;
  color: white;
  background: #27272a;
  border: 2px solid #3f3f46;
  border-radius: 12px;
  outline: none;
  transition: all 0.3s;
}

.input-search input:focus {
  border-color: #8b5cf6;
  background: #1f1f23;
}

.input-search input::placeholder {
  color: #52525b;
}
